import { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {

  constructor(){
    super();

    this.state = {
      monster: [
        {
          name: 'Linda'
        },
        {
          name: 'Frank'
        },
        {
          name: 'James'
        }
      ]
    };
  }

  render() {
    return (
      <div className="App">
        <h1>{this.state.monster1.name}</h1>

      </div>
    );
  }

}

export default App;

